    adjusted_value = parsed_txs["value"] * 10 ** decimals
    print("\033[95m\033[1m" + "\n ADJUSTED VALUE:" + "\033[0m\033[0m")
    print(adjusted_value)

    # check if agent_address has approved the erc20 contract to spend the value
